/**

 * 新增站点注册协议I18N初始数据

 * @author 	Fei

 * @date 	2016-05-04

 * @param 	sid 	站点ID

 * @param 	flag	1-代理，2-玩家

 */

DROP FUNCTION IF EXISTS f_add_reg_pro_i18n(int, int);

create or replace function f_add_reg_pro_i18n(sid int, flag int)

returns void as $$



DECLARE

	rec record;

	i_type text:='service_terms_agent';



BEGIN

	if flag = 2 then

		i_type = 'service_terms';

	end if;



	for rec in

		SELECT module, type, key, locale, value,

			   site_id, remark, default_value, built_in

		  FROM site_i18n

		 WHERE module = 'setting'

		   AND type = 'service_terms'

		   AND key = 'service'

		   AND site_id = 0

	loop

		INSERT INTO site_i18n (

			module, type, key, locale, value,

			site_id, remark, default_value, built_in

		) VALUES (

			rec.module, rec.type, rec.key ,rec.locale,  rec.value,

			sid, rec.remark, rec.default_value, rec.built_in

		);

	end loop;

end;



$$ language plpgsql;

COMMENT ON FUNCTION f_add_reg_pro_i18n(sid int, flag int)

IS 'Fei-新增站点注册协议I18N初始数据';